legacy_settings:
{
	"shortcuts": [
		{
			"regex": "^trigger$",
			"replacement": "## Example replacement\n- [ ] "
		},
		{
			"regex": "^now$",
			"command": "printf `date +%H:%M`"
		},
		{
			"regex": "^py:",
			"command": "echo <text> | cut -c 4- | /Library/Frameworks/Python.framework/Versions/3.8/bin/python3"
		},
		{
			"regex": "^eval:",
			"command": "echo <text> | cut -c 6- | /Library/Frameworks/Python.framework/Versions/3.8/bin/python3 -c 'print(eval(input()), end=\"\")'"
		},
		{
			"regex": "^shell:",
			"command": "echo <text> | cut -c 7- | sh"
		},
		{
			"regex": "^tool:",
			"command": "echo <text> | cut -c 6- | /Library/Frameworks/Python.framework/Versions/3.8/bin/python3 <scripts_path>/tool.py"
		},
		{
			"regex": "^sympy:",
			"command": "echo <text> | cut -c 7- | /Library/Frameworks/Python.framework/Versions/3.8/bin/python3 <scripts_path>/sympy_tool.py"
		}
	],
	"shell": "/bin/sh"
}
